import { toast } from "react-toastify";

const loadJoke = async () => {
  const res = await fetch("https://api.chucknorris.io/jokes/rando");

  if (res.status !== 200) {
    toast.error(`Joke loading failed: ${jokeError}`);
    throw new Error("Not Found");
  }

  return res.json();
};

export default loadJoke;
