import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import loadJoke from '../services/jokeService';

export const jokeReducer = createSlice({
  name: "joke",
  initialState: {
    value: {},
    status: 'loading', 
  },
  reducers: {
    setJoke: (state, action) => {
      state.value = action.payload;
    },
  },
  extraReducers: {

  }
});

export const getJoke = createAsyncThunk('joke', async () => {
  const response = await loadJoke();
  return response;
})

export const { setJoke } = jokeReducer.actions;
export default jokeReducer.reducer;
