import {createSlice} from '@reduxjs/toolkit';

const jokeReducer = createSlice({
    initialState: {
        value: {}
    },
    reducers: {
        setJoke: (state, action) => {
            state.value = action.payload
        }
    }
});

export const {setJoke} = createSlice.action;
export default createSlice.reducer;
