import generateRandomColor from "../helpers";

const loadComments = async (done) => {
  const getComments = await fetch("https://jsonplaceholder.typicode.com/comments")
    .then((res) => (res.json()))
    .then((res) => {
      const comments = res.map((comment) => ({
        ...comment,
        icoColor: generateRandomColor(),
      }));
      if (done) done(comments);
      return comments;
    });

    return getComments;
};

export default loadComments;
