import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import loadJoke from '../services/jokeService';

export const jokeReducer = createSlice({
  name: "joke",
  initialState: {
    value: {},
    status: 'loading', 
    error: null
  },
  reducers: {
    setJoke: (state, action) => {
      state.value = action.payload;
    },
  },
  extraReducers: {
    // [getJoke.pending]: (state) => {
    //   state.status = 'loading';
    // },
    [getJoke.fulfilled]: (state, action) => {
      state.status = 'success';
      state.value = action.payload;
    },
    [getJoke.rejected]: (state, action) => {
      state.status = 'failed';
      state.error = action.error.message; 
    } 
  }
});

export const getJoke = createAsyncThunk('joke', async () => {
  const response = await loadJoke();
  return response;
})

export const { setJoke } = jokeReducer.actions;
export default jokeReducer.reducer;
