import {useDispatch} from 'react-redux';
import {setJoke} from './reducers/jokeReducer';

const dispatch = useDispatch();

const loadJoke = (done) => {
  fetch("https://api.chucknorris.io/jokes/random/")
    .then((res) => res.json())
    .then((res) => {
      done(res);
    });
};

export default loadJoke;
